(1690, 167, 'infotainment-section-bottom', 'a:5:{i:0;a:3:{s:33:\"infotainment-section-bottom-image\";s:3:\"266\";s:33:\"infotainment-section-bottom-title\";s:13:\"Apple Carplay\";s:39:\"infotainment-section-bottom-description\";s:167:\"With Apple Car Play, you can use all your iPhone\'s functions and apps through the Alfa Romeo Stelvio infotainment system. Without ever taking your hands off the wheel.\";}i:1;a:3:{s:33:\"infotainment-section-bottom-image\";s:3:\"267\";s:33:\"infotainment-section-bottom-title\";s:12:\"Android Auto\";s:39:\"infotainment-section-bottom-description\";s:112:\"An extension of your smartphone with Android mobile OS fully integrated into the Alfa Romeo infotainment system.\";}i:2;a:3:{s:33:\"infotainment-section-bottom-image\";s:3:\"264\";s:33:\"infotainment-section-bottom-title\";s:9:\"Telephone\";s:39:\"infotainment-section-bottom-description\";s:162:\"By pairing your smartphone via Bluetooth, you can make or receive calls, as well as play/text, without ever taking your hands off the wheel of Alfa Romeo Stelvio.\";}i:3;a:3:{s:33:\"infotainment-section-bottom-image\";s:3:\"263\";s:33:\"infotainment-section-bottom-title\";s:17:\"Widget / Homepage\";s:39:\"infotainment-section-bottom-description\";s:147:\"Alfa Romeo Stelvio\'s new infotainment system boasts interactive widgets, developed for a more intuitive and, therefore, more immediate interaction.\";}i:4;a:3:{s:33:\"infotainment-section-bottom-image\";s:3:\"263\";s:33:\"infotainment-section-bottom-title\";s:10:\"Navigation\";s:39:\"infotainment-section-bottom-description\";s:92:\"The optimised interface of Alfa Romeo Stelvio\'s navigation system makes it is easier to read\";}}'),
(1691, 167, 'infotainment-section-bottom-image', '266'),
(1692, 167, 'infotainment-section-bottom-title', 'Apple Carplay'),
(1693, 167, 'infotainment-section-bottom-description', 'With Apple Car Play, you can use all your iPhone\'s functions and apps through the Alfa Romeo Stelvio infotainment system. Without ever taking your hands off the wheel.'),
(1694, 167, 'infotainment-section-bottom-image_1', '267'),
(1695, 167, 'infotainment-section-bottom-title_1', 'Android Auto'),
(1696, 167, 'infotainment-section-bottom-description_1', 'An extension of your smartphone with Android mobile OS fully integrated into the Alfa Romeo infotainment system.'),
(1697, 167, 'infotainment-section-bottom-image_2', '264'),
(1698, 167, 'infotainment-section-bottom-title_2', 'Telephone'),
(1699, 167, 'infotainment-section-bottom-description_2', 'By pairing your smartphone via Bluetooth, you can make or receive calls, as well as play/text, without ever taking your hands off the wheel of Alfa Romeo Stelvio.'),
(1701, 167, 'infotainment-section-bottom-image_3', '263'),
(1702, 167, 'infotainment-section-bottom-title_3', 'Widget / Homepage'),
(1703, 167, 'infotainment-section-bottom-description_3', 'Alfa Romeo Stelvio\'s new infotainment system boasts interactive widgets, developed for a more intuitive and, therefore, more immediate interaction.'),
(1705, 167, 'infotainment-section-bottom-image_4', '263'),
(1706, 167, 'infotainment-section-bottom-title_4', 'Navigation'),
(1707, 167, 'infotainment-section-bottom-description_4', 'The optimised interface of Alfa Romeo Stelvio\'s navigation system makes it is easier to read'),
(1709, 167, 'technology-section-right', 'a:3:{i:0;a:3:{s:30:\"technology-section-right-image\";s:3:\"257\";s:30:\"technology-section-right-title\";s:3:\"DNA\";s:36:\"technology-section-right-description\";s:376:\"D, Dynamic mode, for sports driving. Engine and gearbox adopt sporty mapping, traction in curves and sensitivity to the accelerator pedal during acceleration are increased. N, Natural mode, with a standard engine and gearbox response and a more comfortable suspension set-up. A, Advanced Efficiency. For all those occasions when you want to stress efficiency over performance.\";}i:1;a:3:{s:30:\"technology-section-right-image\";s:3:\"258\";s:30:\"technology-section-right-title\";s:12:\"ALFA™ LINK\";s:36:\"technology-section-right-description\";s:390:\"Alfa Romeo Stelvio is equipped with the AlfaTM™ Link suspension system, with a four-and-a-half arm multilink solution in aluminium that translates into superb handling and excellent comfort . At the front, the high quadrilateral suspension with semi-virtual steering axle allows the driver to steer quickly and accurately, courtesy of exceptional road holding and a high sense of control.\";}i:2;a:3:{s:30:\"technology-section-right-image\";s:3:\"256\";s:30:\"technology-section-right-title\";s:9:\"Q4 SYSTEM\";s:36:\"technology-section-right-description\";s:217:\"Alfa Romeo Q4 all-wheel drive technology combines maximum safety with  performance by distributing torque in real time between the front and rear  according to the actual traction conditions and the driver\'s commands.\";}}'),
(1710, 167, 'technology-section-right-image', '257'),
(1711, 167, 'technology-section-right-title', 'DNA'),
(1712, 167, 'technology-section-right-description', 'D, Dynamic mode, for sports driving. Engine and gearbox adopt sporty mapping, traction in curves and sensitivity to the accelerator pedal during acceleration are increased. N, Natural mode, with a standard engine and gearbox response and a more comfortable suspension set-up. A, Advanced Efficiency. For all those occasions when you want to stress efficiency over performance.'),
(1713, 167, 'technology-section-right-image_1', '258'),
(1714, 167, 'technology-section-right-title_1', 'ALFA™ LINK'),
(1715, 167, 'technology-section-right-description_1', 'Alfa Romeo Stelvio is equipped with the AlfaTM™ Link suspension system, with a four-and-a-half arm multilink solution in aluminium that translates into superb handling and excellent comfort . At the front, the high quadrilateral suspension with semi-virtual steering axle allows the driver to steer quickly and accurately, courtesy of exceptional road holding and a high sense of control.'),
(1718, 167, 'technology-section-right-image_2', '256'),
(1719, 167, 'technology-section-right-title_2', 'Q4 SYSTEM'),
(1720, 167, 'technology-section-right-description_2', 'Alfa Romeo Q4 all-wheel drive technology combines maximum safety with  performance by distributing torque in real time between the front and rear  according to the actual traction conditions and the driver\'s commands.'),
